//scrollnav response
    $(".bs-scrollnav-h").each(function() {
        var nav = $(this);
        var outer = $(this).find(".bs-scrollnav");
        var inner = $(this).find(".bs-scrollnav-list");
        var width = 0;
        inner.find("li").each(function() {
            width += parseInt($(this).outerWidth(true));
        });

        if (outer.width() < width) {
            inner.css("width", width);
            outer.css("overflow-x", "auto");
        }
    });
    $(".bs-scrollnav-v").each(function() {
        var nav = $(this);
        $(this).find(".bs-close").click(function() {
            if (nav.hasClass("bs-hide")) {
                nav.removeClass("bs-hide");
            } else {
                nav.addClass("bs-hide");
            }
        });
    });